let articles=document.querySelectorAll(".features__item");Array.prototype.forEach.call(articles,function(e){e.addEventListener("mouseenter",function(){e.classList.add("active")}),e.addEventListener("mouseleave",function(){e.classList.remove("active")})});let burger=document.querySelector(".nav__burger"),navMenu=document.querySelector(".nav__menu");burger.addEventListener("click",function(){navMenu.classList.toggle("close"),burger.classList.toggle("close-menu")});let callFormOpenButton=document.querySelector(".header__consult"),callFormCloseButton=document.querySelector(".form-close"),form=document.querySelector(".overlay");callFormOpenButton.addEventListener("click",function(){form.classList.toggle("close"),callFormCloseButton.addEventListener("click",function(){form.classList.toggle("close")})});